mapper.update:
UPDATE tbl_member
SET profile_img_url = #{profileImgUrl}
WHERE member_email = #{memberEmail}

mapper.select:
select id, member_email, profile_img_url, created_date, updated_date
from tbl_member
where member_email = #{email}

select id, member_email, profile_img_url
from tbl_member
where id = #{id}
