mapper.update:
update tbl_work
set read_count = read_count + 1
where id = #{id}

mapper.select:
select w.id, w.work_price, w.read_count, w.genre_type, p.post_title, p.post_content, p.member_profile_id, p.created_date, p.updated_date, f.file_name as thumbnail_file_name, f.file_path as thumbnail_file_path, mp.profile_nickname as profile_nickname from tbl_work w join tbl_post p on w.id = p.id join tbl_member_profile mp on p.member_profile_id = mp.id left join ( select pf.post_id, f.file_name, f.file_path from tbl_post_file pf join tbl_file f on pf.id = f.id where pf.id = (select max(id) from tbl_post_file where post_id = pf.post_id) ) as f on f.post_id = p.id where w.id is not null
and ( p.post_title like concat('%', #{keyword}, '%') or p.post_content like concat('%', #{keyword}, '%') or mp.profile_nickname like concat('%', #{keyword}, '%') )
and w.genre_type = #{genreType}
order by w.id desc limit #{pagination.startRow}, #{pagination.rowCount}

select count(*) from tbl_work w join tbl_post p on w.id = p.id join tbl_member_profile mp on p.member_profile_id = mp.id where w.id is not null
and ( p.post_title like concat('%', #{keyword}, '%') or p.post_content like concat('%', #{keyword}, '%') or mp.profile_nickname like concat('%', #{keyword}, '%') )
and w.genre_type = #{genreType}
